css:
body, p, em, small, span, b, strong, h1, h2, h3, h4, h5, h6 {
  font-family: 'Dosis', sans-serif
}
.top-bar__nav {
  display: none !important;
}
.header {
  background: #000;
  color: #fff;
}
.header__toggler-icon, .header__buttons i, .header__nav a {
  color: #fff;
}
.header__nav a > h4 {
  font-size: 15px !important;
} 
@media (min-width: 767px) {
  .header__row {
    display: flex;
    flex-direction: row;
  }
  .header__row .col-auto.p-0 {
    order: 1;
  } 
  .header__row .col.col-lg-auto.pl-1.pl-md-2.pl-lg-3 {
    order: 2;
  } 
  .header__row .d-none.d-lg-block.col {
    order: 3
  } 
  .header__row .d-none.d-lg-block.col-12 {
    order: 5;
  }
  .header__row .d-none.d-lg-block.col-12.collapse:not(.show), 
  .header__row .d-none.d-lg-block.col-12.hide.collapse.show {
    display: none !important;
  }
  .header__row .order-lg-last.col-auto.p-0 {
    order: 4;
  }
  .header #mobile-search-btn {
    display: inline-flex !important;
  }
} 
@media (max-width: 767px) {
  .header__row .d-lg-block.col-12.collapse:not(.show), 
  .header__row .d-lg-block.col-12.hide { 
    display: none !important; 
  }
} 
@media screen and (min-width: 1368px) and (max-width: 1550px) { 
  .banner-slider { 
    max-width: 100% !important;
  }
  .max {
    width: 100%;
    margin-top: 2.5rem !important;
  }
} 
@media screen and (min-width: 992px) and (max-width: 1367px) {
  .banner-slider {
    max-width: 100% !important;
  }
  .max {
    width: 100%; margin-top: 2.5rem !important;
  } 
}
 @media screen and (min-width: 481px) and (max-width: 991px) {
   .banner-slider {
     max-width: 100% !important;
   }
} 
.card-header { 
  background: #000;
} 
.card-header .close {
  color: #fff;
} 
.app__header {
  background: #000;
} 
.app__header > * { 
  color: #fff;
} 
.app__contact span {	 
  color: #fff !important;
} 
.app .cart__btn-back {	 
  border: 1px solid #000;
}
.app .discount-applier__coupon-btn {	 
  border: 1px solid #000;
} 
.banner-slider + .responsive-baner .container {
  max-width: 100%;padding: 0;
} 
.footer {	 
  background: #fbe0d7;
}
.footer__title {	 
  text-transform: uppercase;	
  font-weight: 400;	 
  font-size: 20px;
}
.login-modal__btn h4 {	 
  font-size: 1rem !important;
}
