body, p, em, small, span, b, strong, h1, h2, h3, h4, h5, h6 {font-family: 'Dosis', sans-serif}
.top-bar__nav {display: none !important;}
 .header {background: #000;color: #fff;}
 .header__toggler-icon, .header__buttons i, .header__nav a {color: #fff;} 
 .header__nav a > h4 {font-size: 15px !important;} 
 @media (min-width: 767px) {
 .header__row {display: flex;flex-direction: row;} 
 .header__row .col-auto.p-0 {order: 1;} 
 .header__row .col.col-lg-auto.pl-1.pl-md-2.pl-lg-3 {order: 2;} 
 .header__row .d-none.d-lg-block.col {order: 3} 
 .header__row .d-none.d-lg-block.col-12 {order: 5;} 
 .header__row .d-none.d-lg-block.col-12.collapse:not(.show), .header__row .d-none.d-lg-block.col-12.hide.collapse.show {display: none !important;} 
 .header__row .order-lg-last.col-auto.p-0 {order: 4;} 
 .header #mobile-search-btn {display: inline-flex !important;} 
} @media (max-width: 767px) { 
 .header__row .d-lg-block.col-12.collapse:not(.show), .header__row .d-lg-block.col-12.hide { display: none !important; } 
